mapper.select:
SELECT no, email, nickname, gender, birth_date birthDate,
pwd, uuid
FROM members
WHERE email = #{email}
AND pwd = #{pwd}

mapper.update:

	SELECT no
	FROM members
	WHERE email=#{email}

update members
set UUID = #{uuid}
where email = #{email}

update members
set UUID = #{uuid}, pwd = #{pwd}
where no = #{no}
