mapper.select:
SELECT COUNT(*)
FROM likes
WHERE post_no = #{postNo}
AND member_no = #{memberNo}
AND emotion = #{emotion}
AND type = #{type}

mapper.delete:
DELETE
FROM likes
WHERE post_no = #{postNo}
AND member_no = #{memberNo}
AND type= #{type}

mapper.select:
SELECT COUNT(no)
FROM likes
WHERE post_no = #{postNo}
AND member_no = #{memberNo}
AND emotion = #{emotion}
AND type= #{type}
